INSERT INTO `battlepay_display_info` VALUES ('1608', '1604167', '0', '0', '|cffe5cc80Seal of Broken Fate x6', '|cff1eff00Twists fate to provide an opportunity for additional treasure from Legion raid and Mythic dungeon bosses.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1609', '1064188', '0', '0', '|cffe5cc80Veiled Argunite 2.000', '|cff1eff00This dull-colored crystal is highly valued by ethereal traders, especially Thaumaturge Vashreen aboard the Vindicaar.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1610', '1455891', '0', '0', '|cffe5cc80Echoes of Battle 200', '|cff1eff00Used to purchase powerful PvP armor. At the end of the season, this currency converts to gold.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1611', '132775', '0', '0', '|cffe5cc80Nethershard 5.000', '|cff1eff00A fragment of crystallized nether brought to Azeroth by the Legion.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1612', '803763', '0', '0', '|cffe5cc80Legionfall War Supplies 1.000', '|cff1eff00Used to further the construction of valuable structures on the Broken Shore.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1613', '801132', '0', '0', '|cffe5cc80Sightless Eye 2.000', '|cff1eff00A rough, hand-minted coin. It bears a symbol similar to that of the Kirin Tor, with one key difference: the eye is closed.', '', '');
INSERT INTO `battlepay_display_info` VALUES ('1614', '1455894', '0', '0', '|cffe5cc80Echoes of Domination x100', '', '|cff1eff00Used to purchase elite PvP armor. At the end of the season, this currency converts to gold.', '');
INSERT INTO `battlepay_display_info` VALUES ('1630', '1408997', '0', '0', '|cffe5cc80Level 110 Character Boost', '', '|cff1eff00There comes a time in every hero’s quest when they need a little boost to help get them over the hump and back into the action. With a Level 100 Character Boost, you can grant one character a one-time boost to level 90. Bring your hero up to speed and join the fight on the front lines.', '');
INSERT INTO `battlepay_display_info` VALUES ('1631', '1126584', '0', '0', '|cffe5cc80Name Change', '', '|cff1eff00The name you originally chose for your hero doesn’t have to be his name for all eternity. Maybe you have become a little too famous (or notorious), or maybe you just thought of something better; either way, you can change your name and start anew today!', '');
INSERT INTO `battlepay_display_info` VALUES ('1632', '1126583', '0', '0', '|cffe5cc80Faction Change', '', '‘|cff1eff00Traitor’ is such a harsh word… sure, your allies may not be thrilled to see you leave, but what have they ever done for you, really? Maybe it’s time you saw what the other side has to offer. Maybe there are already friends waiting for you to join them. Maybe the epics really are purpler on the other side, as the saying goes. Changing factions also includes a free race change so you will fit right in with your new allies!', '');
INSERT INTO `battlepay_display_info` VALUES ('1633', '939374', '0', '0', '|cffe5cc80Race Change', '', 'I|cff1eff00t ain’t easy being green… or blue… or purplish-pink with pointy ears…. Sometimes a simple touch up of your character’s looks isn’t enough; sometimes you want to go all the way and change which race you belong to! With a race change, you can do just that.', '');
INSERT INTO `battlepay_display_info` VALUES ('1634', '1126582', '0', '0', '|cffe5cc80Appearance Change', '', '|cff1eff00Things change, people change, hairstyles change, interest rates fluctuate… if you’re tired of always wearing a full plate helmet to hide your original character appearance choices, then appearance change is for you! Think of it like the kind of simple do-over people wished existed in real life. Appearance change lets you redo the look and even the gender of one of your characters.', '');
INSERT INTO `battlepay_display_info` VALUES ('1635', '1408997', '0', '0', '|cffe5cc80Premade character(PvE) level 110', '', '|cff1eff00Buying a premade character include :\r\n- ilevel 850 PvE gear.\r\n- ilevel 840 trinkets and cloak\r\n- 20 000 golds\r\n- Instant level 110.\r\n- One mount PvP only: Wrathful Gladiators Frost Wyrm.\r\n- 4 Bags 36 Slot.', '');
