INSERT INTO `battlepay_display_info` VALUES ('1647', '1786422', '0', '0', '|cffe5cc80Unlock Allied Void Elf', '', '|cff1eff00Alleria Windrunner has discovered that one of the blood elves was exiled for studying Void magic. If someone found these renegades, Alleria is convinced that he could train them to become a weapon of the Alliance.', '');
INSERT INTO `battlepay_display_info` VALUES ('1648', '1786420', '0', '0', '|cffe5cc80Unlock Allied Lightforged Draenei', '', '|cff1eff00The Army of Light played a pivotal role in the victory against the Burning Legion at Argus. High Exarch Turalyon has suggested that these Lightforged soldiers would proudly defend the Alliance from their enemies.', '');
INSERT INTO `battlepay_display_info` VALUES ('1649', '1786421', '0', '0', '|cffe5cc80Unlock Allied Nightborne', '', '|cff1eff00After thousands of years of isolation, the Sternamar Nightborns yearn to become part of this world again. Lady Líadrin believes that the Horde would Benefit from an alliance with these ancient elves.', '');
INSERT INTO `battlepay_display_info` VALUES ('1650', '1786419', '0', '0', '|cffe5cc80Unlock Allied Highmountain Tauren', '', '|cff1eff00The Tauren tribes of Monte Alto joined together to drive the Burning Legion out of their lands. Baine Bloodhoof believes that his strength and nobility would be a great contribution to the Horde.', '');
INSERT INTO `battlepay_display_info` VALUES ('1651', '1450455', '0', '0', '|cffe5cc80Draenor and Legion flight', '', '|cff1eff00You learn to fly in the Draenor and Legion zones. + Mount.', '');
INSERT INTO `battlepay_display_info` VALUES ('1652', '1237900', '0', '0', '|cffe5cc80Artifact Power Rank 1', '', '|cff1eff00Add 2 billions artifact power to your weapon.\r\n Increase the level of your artifact weapon to 52. \r\n ', '');
INSERT INTO `battlepay_display_info` VALUES ('1653', '2437242', '0', '0', '|cffe5cc80Rename Guild', '', '|cff1eff00You have to be a Guild master to buy this option \r\n otherwise you will only spend your store points..', '');
